function devNum = findKeyboardDevNums_externalKeyboard% find the device numbers for the keyboard on rd's desk setup for desktop asiago ...% 26 Feb 2009devices = PsychHID('Devices');devNum.Keyboard = []; devNum.Keypad = [];for devicecounter = 1:length(devices)    if (strcmp(devices(devicecounter).product, 'Apple Keyboard') == 1) && (strcmp(devices(devicecounter).usageName, 'Keyboard') == 1)        devNum.Keypad = devicecounter;    endend